(1, 0, 0, 'Main page', '', 0, 0, '', 0, '<!DOCTYPE html>\n<html>\n    [[$head]]\n<body>\n    [[$header]]\n    [[$mobile-menu]]\n    <div class="content">\n        <div class="container">\n            <div class="row flb">\n                [[$left-menu]]\n                <div class="content__main">\n                    <div class="content__slider">\n                        <div class="owl-carousel main-slider">\n                            [[getImageList?\n                                &tvname=`main-slider`\n                                &tpl=`main-slider`\n                                &where=`{"is_published:=":"1"}`\n                            ]]\n                        </div>\n                        <div class="owl-prev"></div>\n                        <div class="owl-next"></div>\n                    </div>\n                    <div class="content__title hidden-sm hidden-xs">НОВИНКИ НАШЕГО АТЕЛЬЕ</div>\n                    <div class="content__mobile-title hidden-lg hidden-md">Добро пожаловать в каталог\n                        продукции компании Lacasa</div>\n                    <div class="content__links">\n                        [[getImageList?\n                            &tvname=`main-blocks`\n                            &tpl=`main-blocks`\n                            &where=`{"is_published:=":"1"}`\n                        ]]\n                    </div>\n                </div>\n            </div>\n        </div>\n    </div>\n    [[$footer]]\n</body>\n</html>', 0, 'a:0:{}', 0, ''),
